__ht__function(g, rect, comp, data, view) {
var imgMap = comp.getValue('imgMap'),
    textAlign = comp.getValue('textAlign'),
    value = comp.getValue('value'),
    gap = comp.getValue('gap'),
    color = comp.getColor('color'),
    iconWidth = comp.getValue('iconWidth'),
    iconHeight = comp.getValue('iconHeight'),
    digitCount = comp.getValue('digitCount'),
    fixed = comp.getValue('fixed');
var x = rect.x,
    y = rect.y,
    width = rect.width,
    height = rect.height;

var drawStretchImage = ht.Default.drawStretchImage;

// format
var str = value + '';
if (str.indexOf('.') > 0 && parseFloat(value)) {
    str = parseFloat(value).toFixed(fixed);
}
if (digitCount && digitCount > str.length) {
    var diff = digitCount - str.length;
    var pre = '';
    for (var i = 0; i < diff; i++) {
        pre += 0;
    }
    str = pre + str;
}
var tx, ty = y;// + (height - iconHeight) / 2;
var ratio = iconWidth / iconHeight;
iconHeight = height;
iconWidth = ratio * iconHeight;
var strWidth = iconWidth * str.length + gap * (str.length - 1);
if (textAlign === 'center') {
    //居中需要判断宽度
    tx = x + (width - strWidth) / 2;
}
else if (textAlign === 'right') {
    tx = x + width - strWidth;
}
else {
    tx = x;
}

for (var i = 0; i < str.length; i++) {
    var n = str.charCodeAt(i);
    var img = imgMap[n];
    if (img) {
        drawStretchImage(g, ht.Default.getImage(img), 'fill', tx, ty, iconWidth, iconHeight, data, view, color);
    }
    tx += iconWidth + gap;
}
}
